import React, { useContext,useState,useCallback } from 'react'
import useLocalStorage from '../hooks/useLocalStorage';
import { useContacts } from './ContactProvider';

const ConversationsContext = React.createContext()

export function useConversations() {
  return useContext(ConversationsContext)
}

export function ConversationProvider({id, children }) {
  const [conversations, setConversations] = useLocalStorage('conversations', [])
  const [selectedConversationIndex, setSelectedConversationIndex] = useState(0)
  const {contacts}=useContacts();

  function createConversation(recipients) {
    setConversations(prevConversation => {
      return [...prevConversation, { recipients,message:[] }]
    })
  }
  const addMessageToConversation = useCallback(({ recipients, text, sender }) => {
    setConversations(prevConversations => {
      let madeChange = false
      const newMessage = { sender, text }
      const newConversations = prevConversations.map(conversation => {
        if (arrayEquality(conversation.recipients, recipients)) {
          madeChange = true
          return {
            ...conversation,
            messages: [...conversation.messages, newMessage]
          }
        }

        return conversation
      })

      if (madeChange) {
        return newConversations
      } else {
        return [
          ...prevConversations,
          { recipients, messages: [newMessage] }
        ]
      }
    })
  })
  function sendMessage(recipients,text){
    addMessageToConversation({recipients,text,sender:id})
  }
  const formmatedConverstions=conversations.map((conversation,index)=>{
    const recipients=conversation.recipients.map(recipient=>{
      const contact=contacts.find(contact=>contact.id===recipient)

      const name=(contact && contact.name) || recipient

      return {id:recipient,name}

    })
    const messages = conversation.messages.map(message => {
      const contact = contacts.find(contact => {
        return contact.id === message.sender
      })
      const name = (contact && contact.name) || message.sender
      const fromMe = id === message.sender
      return { ...message, senderName: name, fromMe }
    })


    const selected = index === selectedConversationIndex
    return { ...conversation, recipients, selected }
  })
  
  const value={
    conversations:formmatedConverstions,
    selectedConversation:formmatedConverstions[selectedConversationIndex],
    sendMessage,
    selectConversationIndex: setSelectedConversationIndex,
    createConversation
  }
     return (
    <ConversationsContext.Provider value={ value }>
      {children}
    </ConversationsContext.Provider>
  )
}
function arrayEquality(a, b) {
    if (a.length !== b.length) return false
  
    a.sort()
    b.sort()
  
    return a.every((element, index) => {
      return element === b[index]
    })
  }
